sql.where:
v.deleted = 0 and t.deleted = 0

    and t.name like CONCAT('%',#{typeName},'%')


    and t.type_code like CONCAT('%',#{typeCode},'%')


    and v.param_value like CONCAT('%',#{paramValue},'%')


    and v.type_id = #{typeId}


    and v.status = #{status}


    and t.id IN

        #{item}

mapper.update:
UPDATE sys_config_type


        type_code = #{typeCode},


        name = #{name},


        sort_number = #{sortNumber},


        deleted = #{deleted}


WHERE id = #{id}
